import produce from "immer"
import { useState } from "react"

export default function CreateVote() {
  var [options, setOptions] = useState(['', ''])

  function remove(idx) {
    setOptions(
      produce(options, options => {
        options.splice(idx, 1)
      })
    )
  }

  function setOption(idx, val) {
    setOptions(
      produce(options, options => {
        options[idx] = val
      })
    )
  }

  return (
    <div>
      <h1>创建投票</h1>
      <div><input type="text" placeholder="投票标题" /></div>
      <div><input type="text" placeholder="补充描述(选填)" /></div>
      {
        options.map((option, idx) => {
          return <div key={idx}>
            <input type="text" placeholder="选项" value={option} onChange={e => setOption(idx, e.target.value)} />
            <button onClick={() => remove(idx)}>删除</button>
          </div>
        })
      }
      <div><button onClick={() => setOptions([...options, ''])}>添加选项</button></div>
    </div>
  )
}
