import axios from "axios"
import { useImmer } from "use-immer"
import { useInput, useBooleanInput } from "../hooks/Hook"
import { useHistory, useLocation } from "react-router-dom"
import { useMemo } from "react"

export default function CreateVote() {
  var [options, setOptions] = useImmer(['', ''])
  var title = useInput()
  var desc = useInput()
  var deadline = useInput()
  var isAnonymous = useBooleanInput()
  var history = useHistory()
  var location = useLocation()
  var params = useMemo(() => new URLSearchParams(location.search), [location.search])

  async function create() {
    var vote = {
      title: title.value,
      desc: desc.value,
      options: options,
      deadline: deadline.value,
      isAnonymous: isAnonymous.value,
    }
    try {
      var res = await axios.post('/create-vote', vote)
      var createdVote = res.data.result
      history.push('/vote/' + createdVote.id)
    } catch (e) {
      throw e
    }
  }

  function remove(idx) {
    setOptions(options => {
      options.splice(idx, 1)
    })
  }

  function setOption(idx, val) {
    setOptions(options => {
      options[idx] = val
    })
  }

  return (
    <div>
      <h1>创建投票</h1>
      <div><input type="text" placeholder="投票标题" {...title} /></div>
      <div><input type="text" placeholder="补充描述(选填)" {...desc} /></div>
      {
        options.map((option, idx) => {
          return <div key={idx}>
            <input type="text" placeholder="选项" value={option} onChange={e => setOption(idx, e.target.value)} />
            <button onClick={() => remove(idx)}>删除</button>
          </div>
        })
      }
      <div><button onClick={() => setOptions(options => { options.push('') })}>添加选项</button></div>
      <div>截止日期:<input type="date" {...deadline} /></div>
      <div>匿名投票:<input type="checkbox" {...isAnonymous} /></div>
      <div><button onClick={create}>创建投票</button></div>
    </div>
  )
}
